import { BasicColumn, FormSchema } from '/@/components/Table';
import { h } from 'vue';
import { router } from '@/router';
import { Image, Tag } from 'ant-design-vue';
import { Icon } from '/@/components/Icon';

//列表数据
export const columns: BasicColumn[] = [
  {
    title: '项目',
    align: 'center',
    dataIndex: 'projectId_dictText',
    fixed: 'left',
    resizable: true,
    customRender: ({ record }) => {
      return h(
        'a',
        {
          onClick: () => {
            router.push({
              path: '/testnet/projectList',
              query: {
                id: record.projectId ? record.projectId : '',
                t: new Date().getTime(),
              },
            });
          },
        },
        record.projectId_dictText ? record.projectId_dictText : ''
      );
    },
  },
  {
    title: 'IP',
    align: 'center',
    dataIndex: 'ip_dictText',
    resizable: true,
    fixed: 'left',
    customRender: ({ record }) => {
      return h(
        'a',
        {
          onClick: () => {
            router.push({
              path: '/testnet/assetIpList',
              query: {
                id: record.ip ? record.ip : '',
                t: new Date().getTime(),
              },
            });
          },
        },
        record.ip_dictText ? record.ip_dictText : ''
      );
    },
  },
  {
    title: '子域名',
    align: 'center',
    resizable: true,
    fixed: 'left',
    dataIndex: 'domain_dictText',
    customRender: ({ record }) => {
      return h('span', [
        h(
          'a',
          {
            onClick: () => {
              router.push({
                path: '/testnet/assetSubDomainList',
                query: {
                  id: record.domain ? record.domain : '',
                  t: new Date().getTime(),
                },
              });
            },
          },
          record.domain_dictText ? record.domain_dictText : ''
        ),
      ]);
    },
  },
  {
    title: '端口/协议',
    align: 'center',
    fixed: 'left',
    width: 100,
    dataIndex: 'portId_dictText',
    resizable: true,
    customRender: ({ record }) => {
      return h('span', [record.portId_dictText, ' ', h(Tag, { color: record.protocol === 'http' ? 'green' : 'blue' }, () => record.httpSchema)]);
    },
  },
  {
    title: 'Icon',
    align: 'center',
    width: 50,
    dataIndex: 'iconUrl',
    customRender(opt) {
      return h(Image, {
        width: 20,
        height: 20,
        src: opt.record.iconUrl ? opt.record.iconUrl : '',
        preview: false,
        fallback:
          'data:image/png;base64,',
      });
    },
  },
  {
    title: '站点标题',
    align: 'center',
    dataIndex: 'webTitle',
    ellipsis: true,
    resizable: true,
    width: 260,
    customRender: ({ record }) => {
      return h('span', [
        h(
          'a',
          {
            href: record.webUrl,
            target: '_blank',
            rel: 'noreferrer',
            style: 'text-decoration: none;', // 移除下划线并继承颜色
          },
          [
            record.webTitle ? record.webTitle : record.webUrl,
            h(Icon, { icon: 'ant-design:link-outlined', style: 'margin-left: 4px;' }), // 调整边距
          ]
        ),
      ]);
    },
  },
  {
    title: '状态码',
    align: 'center',
    width: 80,
    dataIndex: 'statusCode',
  },
  {
    title: '应用/组件',
    align: 'center',
    dataIndex: 'tech',
    width: 200,
    resizable: true,
    customRender: ({ record }) => {
      if (record.tech) {
        const components = JSON.parse(record.tech);
        // 使用Fragment包裹所有的span标签
        return h(
          'div',
          { style: { 'argin-bottom': '10px' } },
          components.map((item, index) => {
            let color = 'default';
            // 根据不同的条件设置不同的颜色
            if (index % 2 === 0) {
              color = 'red';
            } else {
              color = 'blue';
            }
            const tagContent = `${item.name}${item.version ? ` (${item.version})` : ''}`;
            return h(
              Tag,
              {
                color: color,
                style: 'margin-left: 5px;margin-top: 5px;',
              },
              () => tagContent
            );
          })
        );
      } else {
        return h('span', '无数据');
      }
    },
  },
  {
    title: '站点截图',
    align: 'center',
    dataIndex: 'screenshot',
    width: 200,
    resizable: true,
    customRender: ({ record }) => {
      if (!record.screenshot) {
        return h(Image, {
          width: 100,
          height: 100,
          src: '',
          fallback:
            'data:image/png;base64,',
        });
      }
      return h(Image, {
        width: 100,
        height: 100,
        src: record.screenshot,
        fallback:
          'data:image/png;base64,',
      });
    },
  },
  {
    title: '返回包大小',
    align: 'center',
    width: 90,
    dataIndex: 'contentLength',
  },
  {
    title: '延迟',
    align: 'center',
    dataIndex: 'delayTime',
  },
  {
    title: '服务器',
    align: 'center',
    dataIndex: 'webServer',
  },
  {
    title: 'content_type',
    align: 'center',
    dataIndex: 'contentType',
  },
  {
    title: '资产标签',
    align: 'center',
    dataIndex: 'assetLabel_dictText',
  },
  {
    title: '来源',
    align: 'center',
    dataIndex: 'source',
  },
  {
    title: '创建时间',
    align: 'center',
    dataIndex: 'createTime',
    sorter: true,
  },
  {
    title: '更新时间',
    align: 'center',
    dataIndex: 'updateTime',
    sorter: true,
  },
];
